mapper.select:

select userid,passwd,username,birthday,post,addr1,addr2,phone1,phone2,phone3,email1,email2,gradeno,reception
from member
where userid = #{userid} and passwd = #{passwd}

select count(*)
from member
where userid=#{userid}

mapper.delete:
delete
from member
where userid=#{userid} and passwd=#{passwd}

mapper.select:
select count(*)
from member
where userid=#{userid} and username=#{username}

select count(*)
from member
where userid=#{userid} and username=#{username} and email1=#{email1} and email2=#{email2}

mapper.update:
update member
set passwd=#{passwd}
where userid=#{userid}
