mapper.select:
	select passwd
	from member
    where userid = #{userid}

select rpad(substr(userid,0,2),length(userid),'*') as userid1, userid
from member
where username=#{username} and 
phone1=#{phone1} and phone2=#{phone2} and phone3=#{phone3} 
and email1=#{email1} and email2=#{email2}

select count(*)
from member
where userid=#{userid}

mapper.delete:
delete 
from member
where userid=#{userid} and passwd=#{passwd}

mapper.select:
select count(*)
from member
where userid=#{userid} and username=#{username}

select count(*)
from member
where userid=#{userid} and username=#{username} and email1=#{email1} and email2=#{email2}

mapper.update:
update member
set passwd=#{passwd}
where userid=#{userid}
